import random
def getOne(words):
    woahnum = random.randint(0,len(words) - 1)
    return words[woahnum]

def simpleSentence(noun,verb):
    pacman = getOne(noun)
    eats = getOne(verb)
    if pacman == eats:
        return simpleSentence(noun,verb)
    return "the " + pacman + " " + eats

def makeNounPhrase(n,adj):
    woahnum = random.randint(0,3)
    i = 0
    woahAdj = ""
    woahN = getOne(n)
    while i < woahnum:
        woahAdj += getOne(adj) + " "
        i += 1
    a = woahAdj.split()
    if len(a) == 2 and a[0] == a[1]:
        return makeNounPhrase(n,adj)
    if len(a) == 3 and (a[0] == a[2] or a[0] == a[1] or a[1] == a[2]):
        return makeNounPhrase(n,adj)
    if woahN.capitalize() == woahN:
        return woahAdj + woahN
    return "the " + woahAdj + woahN

def makeVerbPhrase(n,adv,v):
    verb = getOne(v)
    noun = getOne(n)
    i = 0
    woahAdv = ""
    woahnum = random.randint(0,3)
    while i < woahnum:
        woahAdv += getOne(adv) + " "
        i += 1
    a = woahAdv.split()
    if len(a) == 2 and a[0] == a[1]:
        return makeVerbPhrase(n,adv,v)
    if len(a) == 3 and (a[0] == a[1] or a[0] == a[2] or a[1] == a[2]):
        return makeVerbPhrase(n,adv,v)
    if verb[len(verb) - 1] == "s":
        return woahAdv + verb + " " + noun
    return makeNounPhrase(v,adv)

def makePoliticalSentence(n,v):
    n1 = getOne(n)
    n2 = getOne(n)
    v1 = getOne(v)
    if n1 == n2:
        return makePoliticalSentence(n,v)
    if v1[len(v1) - 1] == "s":
        v1 = v1[:len(v1) - 1]
    return n1 + " don't " + v1 + " " + n2 + ", " + n2 + " " + v1 + " " + n2

def makePokemonSentence(n,adv,v,adj):
    n1 = getOne(n)
    adv1 = " " + getOne(adv)
    v1 = " " + getOne(v)
    adj1 = " " + getOne(adj)
    return n1 + adv1 + " used" + v1 + ", it's" + adj1

def makeSentence(n,adv,v,adj):
    woahnum = random.randint(0,3)
    if woahnum == 0:
        return simpleSentence(n,v).capitalize()
    if woahnum == 1:
        return makeNounPhrase(n,adj).capitalize()
    if woahnum == 2:
        return makeNounPhrase(n,adj).capitalize() + " " + makeVerbPhrase(n,adv,v)
    if woahnum == 3:
        return makePoliticalSentence(n,v).capitalize()

